type Info struct {
	Id                string      `json:"id"`
	UrlToken          string      `json:"url_token"`
	Name              string      `json:"name"`
	UseDefaultAvatar  bool        `json:"use_default_avatar"`
	AvatarUrl         string      `json:"avatar_url"`
	AvatarUrlTemplate string      `json:"avatar_url_template"`
	IsOrg             bool        `json:"is_org"`
	Type              string      `json:"type"`
	Url               string      `json:"url"`
	UserType          string      `json:"user_type"`
	Headline          string      `json:"headline"`
	HeadlineRender    string      `json:"headline_render"`
	Gender            int         `json:"gender"`
	IsAdvertiser      bool        `json:"is_advertiser"`
	IpInfo            interface{} `json:"ip_info"`
	VipInfo           struct {
		IsVip           bool        `json:"is_vip"`
		VipType         int         `json:"vip_type"`
		RenameDays      string      `json:"rename_days"`
		EntranceV2      interface{} `json:"entrance_v2"`
		RenameFrequency int         `json:"rename_frequency"`
		RenameAwaitDays int         `json:"rename_await_days"`
		VipIcon         struct {
			Url          string `json:"url"`
			NightModeUrl string `json:"night_mode_url"`
		} `json:"vip_icon,omitempty"`
	} `json:"vip_info"`
	Badge   []interface{} `json:"badge"`
	BadgeV2 struct {
		Title        string        `json:"title"`
		MergedBadges []interface{} `json:"merged_badges"`
		DetailBadges []interface{} `json:"detail_badges"`
		Icon         string        `json:"icon"`
		NightIcon    string        `json:"night_icon"`
	} `json:"badge_v2"`
	IsFollowing          bool `json:"is_following"`
	IsFollowed           bool `json:"is_followed"`
	FollowerCount        int  `json:"follower_count"`
	AnswerCount          int  `json:"answer_count"`
	ArticlesCount        int  `json:"articles_count"`
	AvailableMedalsCount int  `json:"available_medals_count"`
	IsRealname           bool `json:"is_realname"`
	HasApplyingColumn    bool `json:"has_applying_column"`
}
type User struct {
	username string
	avatar   string
	place    string
	intro    string
}

